require 'net/http'
require 'uri'
require 'cgi'

#OpenSSL::SSL::VERIFY_PEER = OpenSSL::SSL::VERIFY_NONE

access_token = 'ya29.SgCiEU3tWBZfcxoAAACBfw6ltiaf1W7KT-lEPI_8QS8G0ylL4__qfXasw9vyZQ'
url = "https://picasaweb.google.com/data/feed/api/user/default/albumid/6038486047401264337"
uri = URI.parse url
req = Net::HTTP::Post.new uri.request_uri
req['Content-Type'] = 'multipart/related; boundary="END_OF_POINT"'
req['Authorization'] = "Bearer #{access_token}"

image = CGI.escape ""

post_body = <<"EOS"
